import { FadeInExpand } from "@/components/animations/FadeInExpand";
import { useState } from "react";
import Confetti from "react-confetti";
import { FaLock } from "react-icons/fa";
import { IoMdClose } from "react-icons/io";

const BLUR_CLASS = `
  bg-gray-500 bg-clip-padding backdrop-filter  backdrop-blur bg-opacity-10 backdrop-saturate-100 backdrop-contrast-100 bg-[url('data:image/svg+xml;base64,')] bg-blend-overlay
 `;
export function ProfileActivationOverlay() {
  const [showForm, setShowForm] = useState(false);
  function toggleForm() {
    setShowForm(!showForm);
  }
  if (!showForm) {
    return (
      <div className="flex justify-center items-center">
        <button
          type="button"
          className="absolute bottom-8 rounded-lg p-4 shadow-lg duration-300 hover:scale-125 z-10"
          aria-label="Scroll down"
          onClick={toggleForm}
        >
          <FadeInExpand className="w-50 h-50">
            <FaLock className="text-4xl text-white opacity-40 hover:opacity-100 duration-200" />
            <Confetti className="w-full h-full" />
          </FadeInExpand>
        </button>
      </div>
    );
  }
  return (
    <div
      className={`flex justify-center items-center absolute w-dvw h-dvh bg-black z-10 ${BLUR_CLASS}`}
    >
      <button
        className="top-5 right-5 absolute bg-white rounded-full h-10 w-10 hover:scale-125 duration-300 flex justify-center items-center"
        type="button"
        onClick={toggleForm}
      >
        <IoMdClose />
      </button>
      <FadeInExpand>
        <input
          className="rounded-lg p-4 shadow-lg bg-gray-100 outline-none text-gray-700 text-xl font-bold"
          placeholder="Секрет"
        />
      </FadeInExpand>
    </div>
  );
}
